import 'package:cloud_firestore/cloud_firestore.dart';import 'package:flutter/material.dart';import 'package:vendor/firebase/model/pricing_model.dart';class PricingController{  //static  static Future<PricingModel> getPricing()async{    // Reference to the "users" collection    DocumentSnapshot pricingData = await FirebaseFirestore.instance.collection('admin').doc("admin_397438").collection("admin_pricing").doc("admin_pricing").get();    PricingModel pricingModel = PricingModel.fromJson(pricingData as Map<String, dynamic>);    return pricingModel;  }}