import 'package:flutter/material.dart';class PaginationWidget extends StatelessWidget {  final int totalPages;  PaginationWidget({required this.totalPages});  @override  Widget build(BuildContext context) {    return Container(      padding: EdgeInsets.all(16.0),      child: Row(        mainAxisAlignment: MainAxisAlignment.center,        children: List.generate(          totalPages,              (index) => GestureDetector(            onTap: () {              // Handle page selection            },            child: Container(              margin: EdgeInsets.symmetric(horizontal: 8.0),              padding: EdgeInsets.all(8.0),              decoration: BoxDecoration(                color: Colors.blue,                borderRadius: BorderRadius.circular(5.0),              ),              child: Text(                (index + 1).toString(),                style: TextStyle(color: Colors.white),              ),            ),          ),        ),      ),    );  }}